import { useState } from 'react';
import './App.css';
function App() {
    const [todos, setTodos] = useState([
        'Make a react project',
        'Record a coding video.'
    ])
    const [input, setInput] = useState("")

    const addTodo = e => {
        e.preventDefault()
        setTodos([...todos, input])
        setInput("")
    }
    return (
        <div className="App" style={{marginLeft: "40px"}}>
            <h1 style={{color: "rebeccapurple"}}>TODO React Firebase</h1>
            <form>
                <input value={input} onChange= {e => setInput(e.target.value)} style={{
                    height: "40px",
                    width: "30%",
                    borderRadius: "25px 0 0 25px",
                    borderColor: "1px solid rebeccapurple"
                }}/>
                <button type='submit' onClick={addTodo} 
                style={{backgroundColor: "rebeccapurple",
                padding: "10px",
                fontFamily: "cursive",
                fontSize: "20px",
                outline: "none",
                border: "none",
                borderRadius: "25px",
                marginLeft: "10px" }}>
                    Add Todo</button>
            </form>
            <ul>
                {todos.map(todo => <li>{todo}</li>)}
            </ul>
        </div>
    );
}
export default App;
